import React, { useEffect, useState } from 'react';
import Classes from './home.module.css';

const Categories = (props) => {
  const [categories, setCategories] = useState([]);

  const getCategories = async () => {
    const respond = await fetch('http://localhost:8000/api/categories');
    const result = await respond.json();
    setCategories(result.data);
    console.log(result);
  };

  useEffect(() => {
    getCategories();
  }, []);

  return (
    <div>
      {categories.map((cat, cKey) => (
        <div key={cKey} className={Classes.container}>
          <Link
            to={{
              pathname: `/carzy-shop/${cat.id}/${cat.name}`,
            }}
          >
            <img
              src='https://images.unsplash.com/photo-1612832021026-375ae70f24bf?ixid=MXwxMjA3fDF8MHxlZGl0b3JpYWwtZmVlZHwxMXx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60'
              //{cat.image}
              alt='Avatar'
              className={Classes.image}
              style={{ width: '100%' }}
            />
          </Link>
          <div className={Classes.middle}>
            <div className='text'>{cat.name}</div>
          </div>
        </div>
      ))}
    </div>
  );
};

